async function pinterest(t) {
  return new Promise(async (e, a) => {
    axios
      .get("https://id.pinterest.com/search/pins/?autologin=true&q=" + t, {
        headers: {
          cookie:
            '_auth=1; _b="AVna7S1p7l1C5I9u0+nR3YzijpvXOPc6d09SyCzO+DcwpersQH36SmGiYfymBKhZcGg="; _pinterest_; _ir=0',
        },
      })
      .then(({ data: t }) => {
        const a = cheerio.load(t),
          i = [],
          c = [];
        a("div > a")
          .get()
          .map((t) => {
            const e = a(t).find("img").attr("src");
            i.push(e);
          }),
          i.forEach((t) => {
            null != t && c.push(t.replace(/236/g, "736"));
          }),
          c.shift(),
          e(c);
      });
  });
}

module.exports.pinterest = pinterest